using System.Reflection;using Edu.StockApi.Web.Configuration.Middleware;using Edu.StockApi.Web.Services;using Microsoft.OpenApi.Models;namespace Edu.StockApi.Web;public class Startup{    public void ConfigureServices(IServiceCollection services)    {        //  Добавление контроллеров        services.AddControllers();                //   DI        services.AddSingleton<IStockService, StockService>();        //  добавление Swagger        services.AddSwaggerGen(options =>        {            //  Описание свагера приложения            options.SwaggerDoc("v1", new OpenApiInfo(){Title = "Edu.StockApi", Version = "v1"} );                        //  Добавление документации в сваггер            var xmlFileName = Assembly.GetExecutingAssembly().GetName().Name + ".xml";            var xmlFilePath = Path.Combine(AppContext.BaseDirectory, xmlFileName);            options.IncludeXmlComments(xmlFilePath);                        //  Добавить фильтр header            options.OperationFilter<HeaderOperationFilter>();        });    }        public void Configure(IApplicationBuilder app)    {        //  Настройка middleware переход        app.Map("/version", builder => builder.UseMiddleware<VersionMiddleware>());                //  Добавить сваггер        app.UseSwagger();        app.UseSwaggerUI();                //  Добавить роутинг        app.UseRouting();                //  Добавить роутинг        app.UseEndpoints(endpoints =>        {            endpoints.MapControllers();        });    }}